con:config.script:
// import Java Excel API library import jxl.* import jxl.write.* def TestCase = context.testCase def FilePath = "C:\\Testdata\\Testdata.xls" def count Workbook WorkBook1 = Workbook.getWorkbook(new File(FilePath)) Sheet Sheet1 = WorkBook1.getSheet(0) PropertiesTestStep = TestCase.getTestStepByName("Properties") count = PropertiesTestStep.getPropertyValue("counter").toInteger() //If Total records is unknown (at start), get the rowcount from Excel if (PropertiesTestStep.getPropertyValue("total").toString() == "") PropertiesTestStep.setPropertyValue("total", Sheet1.getRows().toString()) count++ //Read the Excel test data Cell Field1 = Sheet1.getCell(0, count) Cell Field2 = Sheet1.getCell(1, count) log.info ("Count is " + count.toString() + " Int A : " + Field1.getContents() + " Int B : " + Field2.getContents())
//Close workbook. WorkBook1.close()
//Copy the Excel test data to properties in Properties test step PropertiesTestStep.setPropertyValue("intA", Field1.getContents()) PropertiesTestStep.setPropertyValue("intB", Field2.getContents()) PropertiesTestStep.setPropertyValue("counter", count.toString()) // check the counter value is less than total no of rows. If same set prop 'end' to 'true' if (count == PropertiesTestStep.getPropertyValue("total").toInteger() - 1) PropertiesTestStep.setPropertyValue("end", "true")

// declare context, property step
def TestCase = context.testCase
PropertiesTestStep = TestCase.getTestStepByName("Properties")
// get prop value for 'end'
Stop = PropertiesTestStep.getPropertyValue("end").toString()
if (Stop=="true")
    log.info("Data Loop")
else
    testRunner.gotoStepByName("Data Source")
